      if (cache[nn] && cache[nn].length > nk) {
        return cache[nn][nk]
      }
      /* Fill the cache */
      for (let m = 0; m <= nn; ++m) {
        if (!cache[m]) {
          cache[m] = [m === 0 ? make(1) : make(0)]
        }
        if (m === 0) continue
        const row = cache[m]
        const prev = cache[m - 1]
